WebLink.url:
{!REQUIRESCRIPT('/resource/sked_ARC_Dist/scripts/sked-modal-helper.js')}

var iframe_url = '{!URLFOR("/apex/skedFullServiceBookingGrid")}'; 

var modalHelper = new skedUtil.ModalHelper('/resource/sked_ARC_Vendors');

// show modal
var modalController = modalHelper.loadIframeModal(iframe_url, {
	rootRecordId: '{!Training_Events__c.Id}' 
});

// Listen to message from child IFrame window 
modalController.registerModalEvent(function (event) { 
	var data = event.data,
		message = data.message;

	if (message === 'cancel') {
		modalController.closeModal();
	} else if (message === 'loaded') {
		modalController.hideIframeLoading();
	} else if (message === 'done') {
		modalController.showIframeLoading();
		window.location.reload();
		//modalController.closeModal();
	}
});
